import { useState } from 'react';
import './App.css';
import gptLogo from './assets/chatgpt.svg'; 
import addBtn from './assets/add-30.png'; 
import msgIcon from './assets/message.svg'; 
import home from './assets/home.svg'; 
import saved from './assets/bookmark.svg'; 
import rocket from './assets/rocket.svg'; 
import sendBtn from './assets/send.svg'; 
import userIcon from './assets/user-icon.png'; 
import gptImgLogo from './assets/chatgptLogo.svg'; 

function App() {
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState({
    text: "Hi, I’m ChatGPT, an AI assistant created by OpenAI. I can help with a wide range of tasks like answering questions, brainstorming ideas, writing, coding, and offering advice. Let me know how I can assist you!",
    isBot: true,
  });
  const [response, setResponse] = useState("");

  const handleSend = async () => {
    if (!input.trim()) return; // Prevent sending an empty message
    try {
      const res = await fetch('http://localhost:5000/api/sendMessage', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message: input }),
      });
      const result = await res.json();
      setResponse(result); // Store response to display
      console.log(result); // Log response for debugging
    } catch (error) {
      console.error("Error sending message:", error);
    }
    setMessages([
      ...messages,
      { text: input, isBot: false },
      { text: res, isBot: true }
    ]);
  }

  return (
    <div className="App">
      <div className="sideBar">
        <div className="upperSide">
          <div className="upperSideTop">
            <img src={gptLogo} alt="Logo" className="logo" />
            <span className="brand">Chatgpt</span>
          </div>
          <button className="midBtn">
            <img src={addBtn} alt="new chat" className="addBtn" />
            New Chat
          </button>
          <div className="upperSideBottom">
            <button className="query">
              <img src={msgIcon} alt="Query" />
              What is Programming?
            </button>
            <button className="query">
              <img src={msgIcon} alt="Query" />
              How to use an API?
            </button>
          </div>
        </div>
        <div className="lowerSide">
          <div className="listItems">
            <img src={home} alt="Home" className="listItemsImg" />
            Home
          </div>
          <div className="listItems">
            <img src={saved} alt="Saved" className="listItemsImg" />
            Saved
          </div>
          <div className="listItems">
            <img src={rocket} alt="Upgrade" className="listItemsImg" />
            Upgrade to Pro
          </div>
        </div>
      </div>
      <div className="main">
        <div className="chats">
          <div className="chat">
            <img className='chatImg' src={userIcon} alt="" />
            <p className="txt">
              Lorem ipsum dolor sit, amet consectetur adipisicing elit. 
              Accusantium iure eius eveniet iusto nobis ipsa recusandae rem 
              deserunt reiciendis id!
            </p>
          </div>
          <div className="chat bot">
            <img className='chatImg' src={gptImgLogo} alt="" />
            <p className="txt">
              Lorem ipsum dolor, sit amet consectetur adipisicing elit. Vero error maxime officia. Explicabo modi suscipit fugiat dolor rerum voluptas sequi labore possimus perferendis excepturi. Tempore voluptatum quidem deleniti eveniet asperiores deserunt repellendus consectetur, dolorem fugiat. Dignissimos, voluptatem iusto ipsum iste voluptates velit ducimus vitae veritatis alias maiores praesentium ad cupiditate, autem doloremque! Sapiente sed nobis error et modi necessitatibus, blanditiis deleniti quis mollitia corrupti, accusantium facere quasi minima iure explicabo delectus quod quibusdam sit reprehenderit, itaque dolorum aut. Quibusdam pariatur error dolor possimus perferendis totam similique quis sed ab maiores. Recusandae praesentium accusamus nobis deleniti saepe, consequatur blanditiis expedita ad!
            </p>
          </div>
          {messages.map((message, i) => {
            <div className="chat bot">
              <img className='chatImg' src={gptImgLogo} alt="" />
              <p className="txt">
                { message.text }
              </p>
            </div>
          })}
        </div>
        <div className="chatFooter">
          <div className="inp">
            <input 
              type="text" 
              placeholder='Send a message' 
              value={input} 
              onChange={(e) => setInput(e.target.value)} 
            />
            <button className="send" onClick={handleSend}>
              <img src={sendBtn} alt="Send" />
            </button>
          </div>
          <p>ChatGPT can make mistakes. Check important info.</p>
        </div>
      </div>
    </div>
  );
}

export default App;
