const {Configuration, OpenAIApi} = require ('openai');
const configuration = new configuration({apiKey: "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"});
const openai = new OpenAIApi(configuration);

export async function sendMsgToOpenAI(message) {
    const res = await openai.createCompletion({
        model:'text-davinci-003',
        prompt: message,
        temperature: 0.7,
        max_tokens: 256,
        top_p: 1,
        frequency_penalty: 0,
        presence_penalty: 0
    });
    return res.data.choice[0].text;
}
